  return gulp.src('./dist/web/*-*.html')
    .pipe(rename(function(path){
        var basename = path.basename.split('-');
        if (basename.length > 1) {
            basename.pop();
            path.dirname = '/temp'
            path.basename = basename.join('-');
            path.extname = '.html';
        }
    }))
    .pipe(gulp.dest('./dist'))
});

//build
