    restore: true
  });
  var jsFilter = filter('./dist/js/*.js', {
    restore: true
  });
  var date = new Date();
  var times = date.getFullYear() + '-' + (date.getMonth() + 1) + '-' + date.getDate() + '   ' + date.getHours() + ':' + date.getMinutes() + ':' + date.getSeconds();
